const burgerBtn=document.querySelector("#burgerBtn"),burgerSection=document.querySelector(".section.burger"),body=document.querySelector("body");let isBurgerOpen=!1;const handleBurgerTouch=()=>{isBurgerOpen?(burgerSection.classList.remove("isActive"),burgerBtn.classList.remove("isActive"),body.classList.remove("blocked")):(burgerSection.classList.add("isActive"),burgerBtn.classList.add("isActive"),body.classList.add("blocked")),isBurgerOpen=!isBurgerOpen};burgerBtn.addEventListener("click",handleBurgerTouch);